@DevDescribe("折叠整理")
public class CoordinatorActivity2 extends AppCompatActivity {
    private static final String TAG = "CoordinatorActivity";
    private String imgString2 = "";
//    private  String testImg = " ";
    private WebView mWebView;
    private ImageView mImg;

    @Override
    protected void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        getWindow().clearFlags(WindowManager.LayoutParams.FLAG_SECURE);
        setContentView(R.layout.activity_coordinater);
        mImg = findViewById(R.id.img);

        mWebView = findViewById(R.id.web_view);
        mWebView.loadUrl("https://www.jianshu.com/p/06c0ae8d9a96");
//        1.CoordinatorLayout
//        2.AppBarLayout
//        3.CollapsingToolbarLayout
//        4.Toolbar
//        5.layout_behavior
//        6.FloatingActionButton

        Bitmap bitmap = stringToBitmap(imgString2);
        Log.d(TAG, "onCreate: bitmap = "+ bitmap);
        if(bitmap != null){
            mImg.setImageBitmap(bitmap);
        }
    }



    //    TODO 1:将字符串转换成Bitmap类型（Base64字符串转换成图片）
    public Bitmap stringToBitmap(String imgBase64){
        Bitmap bitmap=null;
        try {
            byte[]bitmapArray;
            bitmapArray= Base64.decode(imgBase64, Base64.DEFAULT);
            bitmap= BitmapFactory.decodeByteArray(bitmapArray, 0, bitmapArray.length);
        } catch (Exception e) {
            e.printStackTrace();
        }
        return bitmap;
    }

}
